SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualiza_desc_cir_adendo (nr_cirurgia_p bigint, nr_sequencia_p bigint) AS $body$
DECLARE


ds_diagnostico_w	varchar(2000);
ds_diagnostico_pos_w	varchar(2000);
ds_resumo_cirurgia_w	varchar(2000);
ds_tecnica_anestesica_w	varchar(2000);
ds_exame_rad_w		varchar(2000);
ds_exame_anatomo_w	varchar(2000);
ds_cirurgia_w		text;
nr_seq_principal_w	bigint;


BEGIN

if (coalesce(nr_cirurgia_p,0) > 0) and (coalesce(nr_sequencia_p,0) > 0) then
	select	max(nr_sequencia)
	into STRICT	nr_seq_principal_w
	from	cirurgia_descricao
	where	nr_cirurgia = nr_cirurgia_p
	and	ie_tipo_descricao = 'P';

	if (nr_seq_principal_w IS NOT NULL AND nr_seq_principal_w::text <> '') then
		select	ds_diagnostico,
			ds_diagnostico_pos,
			ds_resumo_cirurgia,
			ds_tecnica_anestesica,
			ds_exame_rad,
			ds_exame_anatomo,
			ds_cirurgia
		into STRICT	ds_diagnostico_w,
			ds_diagnostico_pos_w,
			ds_resumo_cirurgia_w,
			ds_tecnica_anestesica_w,
			ds_exame_rad_w,
			ds_exame_anatomo_w,
			ds_cirurgia_w
		from	cirurgia_descricao
		where	nr_sequencia = nr_seq_principal_w;

		update	cirurgia_descricao
		set	ds_diagnostico		= ds_diagnostico_w,
			ds_diagnostico_pos 	= ds_diagnostico_pos_w,
			ds_resumo_cirurgia 	= ds_resumo_cirurgia_w,
			ds_tecnica_anestesica 	= ds_tecnica_anestesica_w,
			ds_exame_rad 		= ds_exame_rad_w,
			ds_exame_anatomo 	= ds_exame_anatomo_w,
			ds_cirurgia 		= ds_cirurgia_w
		where	nr_sequencia		= nr_sequencia_p
		and	ie_tipo_descricao	= 'A'
		and	coalesce(ds_diagnostico::text, '') = ''
		and	coalesce(ds_diagnostico_pos::text, '') = ''
		and	coalesce(ds_resumo_cirurgia::text, '') = ''
		and	coalesce(ds_tecnica_anestesica::text, '') = ''
		and	coalesce(ds_exame_rad::text, '') = ''
		and	coalesce(ds_exame_anatomo::text, '') = '';
		--and	ds_cirurgia 		is null;
		commit;
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
