SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualiza_doc_conv_partic (NR_SEQUENCIA_P bigint, NM_USUARIO_P text) AS $body$
DECLARE


nr_doc_convenio_w             	varchar(20);
IE_DOC_CONV_PART_FUNC_w		varchar(01);
cd_convenio_w			bigint;
cd_categoria_w			varchar(20);
nr_atendimento_w		bigint;
cd_estabelecimento_w		bigint;


BEGIN

begin
select	cd_convenio,
	cd_categoria,
	nr_doc_convenio,
	nr_atendimento
into STRICT	cd_convenio_w,
	cd_categoria_w,
	nr_doc_convenio_w,
	nr_atendimento_w
from	procedimento_paciente
where	nr_sequencia	= nr_sequencia_p;
exception
	when others then
		nr_doc_convenio_w	:= null;
end;


if (nr_doc_convenio_w IS NOT NULL AND nr_doc_convenio_w::text <> '') then

	select	max(cd_estabelecimento)
	into STRICT	cd_estabelecimento_w
	from	atendimento_paciente
	where	nr_atendimento	= nr_atendimento_w;

	begin
	select	coalesce(max(IE_DOC_CONV_PART_FUNC), 'N')
	into STRICT	IE_DOC_CONV_PART_FUNC_w
	from	convenio_estabelecimento
	where	cd_convenio		= CD_CONVENIO_W
	and	cd_estabelecimento	= cd_estabelecimento_w;
	exception
		when others then
			IE_DOC_CONV_PART_FUNC_w	:= 'N';
	end;


	if (IE_DOC_CONV_PART_FUNC_w = 'S') then
		update	procedimento_participante
		set	NR_DOC_HONOR_CONV	=
				nr_doc_convenio_w || lpad(ie_funcao,3,0)
		where	 nr_sequencia		= nr_sequencia_p;
	end if;


end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
