CREATE OR REPLACE PROCEDURE atualiza_dt_conta_item_devol ( nr_devolucao_p bigint, nr_item_devolucao_p bigint, ie_opcao_p text, nm_usuario_p text) AS $body$
DECLARE


/*
ie_opcao_p
	L - Liberacao
	E - Estorno liberação
*/
dt_baixa_conta_w		timestamp;
nm_usuario_baixa_conta_w	varchar(15);


BEGIN
if (ie_opcao_p = 'L') then
	dt_baixa_conta_w		:= clock_timestamp();
	nm_usuario_baixa_conta_w	:= nm_usuario_p;
elsif (ie_opcao_p = 'E') then
	dt_baixa_conta_w		:= null;
	nm_usuario_baixa_conta_w	:= '';
end if;

if (coalesce(nr_item_devolucao_p,0) > 0) then
	begin
	update	item_devolucao_material_pac
	set	dt_baixa_conta		= dt_baixa_conta_w,
		nm_usuario_baixa_conta	= nm_usuario_baixa_conta_w
	where	nr_devolucao		= nr_devolucao_p
	and	nr_sequencia		= nr_item_devolucao_p;
	end;
elsif (coalesce(nr_item_devolucao_p,0) = 0) then
	begin
	update	item_devolucao_material_pac
	set	dt_baixa_conta		= dt_baixa_conta_w,
		nm_usuario_baixa_conta	= nm_usuario_baixa_conta_w
	where	nr_devolucao		= nr_devolucao_p;
	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
