CREATE OR REPLACE PROCEDURE atualiza_dt_deposito_cheque ( dt_deposito_p timestamp, nr_seq_cheque_p bigint, nm_usuario_p text) AS $body$
BEGIN
if (dt_deposito_p IS NOT NULL AND dt_deposito_p::text <> '') and (nr_seq_cheque_p IS NOT NULL AND nr_seq_cheque_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin
	update 	cheque_cr
	set    	dt_deposito = dt_deposito_p,
		nm_usuario = nm_usuario_p,
		dt_atualizacao = clock_timestamp()
	where  	nr_seq_cheque  = nr_seq_cheque_p;

	--gerar_cheque_cr_hist(nr_seq_cheque_p,'O cheque foi depositado.','N',nm_usuario_p);
	CALL gerar_cheque_cr_hist(nr_seq_cheque_p,wheb_mensagem_pck.get_texto(304470),'N',nm_usuario_p);
	CALL liberar_cheque_orgao_cobr(nr_seq_cheque_p,nm_usuario_p);
	end;
end if;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
