laudo	bigint;
	
C01 CURSOR FOR 
	SELECT	nr_sequencia, 
		dt_prev_execucao, 
		nr_seq_proc_interno, 
		cd_procedimento, 
		ie_origem_proced, 
		cd_setor_atendimento 
	from	prescr_procedimento 
	where	nr_prescricao = nr_prescricao_p 
	order by dt_resultado;
			

BEGIN 
 
update	prescr_medica 
set	dt_entrega = clock_timestamp() 
where	nr_prescricao = nr_prescricao_p;
 
open C01;
loop 
fetch C01 into	 
	nr_sequencia_w, 
	dt_prev_execucao_w, 
	nr_seq_proc_interno_w, 
	cd_procedimento_w, 
	ie_origem_proced_w, 
