	dt_entrega_w := obter_entrega_laudo(nr_prescricao_p, dt_prev_execucao_w, nr_seq_proc_interno_w, cd_procedimento_w, ie_origem_proced_w, cd_setor_atendimento_w, nm_usuario_p, dt_entrega_w);
	 
	update	prescr_procedimento 
	set	dt_resultado = dt_entrega_w 
	where	nr_prescricao = nr_prescricao_p 
	and	nr_sequencia = nr_sequencia_w;
		 
	end;
end loop;
