SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualiza_dt_registro_cheque ( nr_seq_cheque_p bigint, dt_registro_p timestamp, nm_usuario_p text) AS $body$
DECLARE


dt_registro_w		timestamp;
ds_historico_w		varchar(4000);


BEGIN

if (nr_seq_cheque_p IS NOT NULL AND nr_seq_cheque_p::text <> '') and (dt_registro_p IS NOT NULL AND dt_registro_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then

	select	max(a.dt_registro)
	into STRICT	dt_registro_w
	from	cheque_cr a
	where	a.nr_seq_cheque	= nr_seq_cheque_p;

	update 	cheque_cr
	set    	dt_registro 	= dt_registro_p,
		nm_usuario  	= nm_usuario_p,
		dt_atualizacao  = clock_timestamp()
	where  	nr_seq_cheque   = nr_seq_cheque_p;

	ds_historico_w	:=	substr(wheb_mensagem_pck.get_texto(304501,'DT_ANTERIOR=' || to_char(dt_registro_w,'dd/mm/yyyy') ||
									';DT_ATUAL=' || to_char(dt_registro_p,'dd/mm/yyyy')),1,4000);

	CALL gerar_cheque_cr_hist(	nr_seq_cheque_p,
				ds_historico_w,
				'N',
				nm_usuario_p);

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
