CREATE OR REPLACE PROCEDURE atualiza_duracao_proc_paciente ( nr_sequencia_p bigint, qt_minuto_duracao_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


dt_inicio_procedimento_w	timestamp;


BEGIN

if (coalesce(qt_minuto_duracao_p,0) > 0) and (coalesce(nr_sequencia_p,0) > 0) then
	select	max(obter_data_proc_exec(dt_procedimento,qt_minuto_duracao_p))
	into STRICT	dt_inicio_procedimento_w
	from	procedimento_paciente
	where	nr_sequencia = nr_sequencia_p;

	update	procedimento_paciente
	set	dt_inicio_procedimento	= dt_inicio_procedimento_w,
		nm_usuario		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp()
	where	nr_sequencia 		= nr_sequencia_p;

	commit;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
