CREATE OR REPLACE PROCEDURE atualiza_entrega_prescr_atend ( nr_atendimento_p bigint, nr_seq_forma_laudo_p bigint, nm_usuario_p text) AS $body$
DECLARE

						 
nr_seq_forma_laudo_old_w	bigint;
nr_prescricao_w		bigint;
ie_atualiza_dt_entrega_w	varchar(1);
ie_forma_atualizacao_w	varchar(1);
		
C01 CURSOR FOR 
	SELECT distinct a.nr_prescricao 
	FROM prescr_medica a
LEFT OUTER JOIN prescr_procedimento b ON (a.nr_prescricao = b.nr_prescricao)
WHERE ((ie_forma_atualizacao_w = 'N' and (b.nr_prescricao IS NOT NULL AND b.nr_prescricao::text <> '')) or ie_forma_atualizacao_w = 'S') and a.nr_atendimento = nr_atendimento_p;
		

BEGIN 
 
ie_atualiza_dt_entrega_w := Obter_Param_Usuario(916, 645, obter_perfil_ativo, nm_usuario_p, obter_estabelecimento_ativo, ie_atualiza_dt_entrega_w);
ie_forma_atualizacao_w := Obter_Param_Usuario(916, 1149, obter_perfil_ativo, nm_usuario_p, obter_estabelecimento_ativo, ie_forma_atualizacao_w);
 
open C01;
	loop 
	fetch C01 into	 
		nr_prescricao_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin 
		 
		select	coalesce(max(nr_seq_forma_laudo),0) 
		into STRICT	nr_seq_forma_laudo_old_w 
		from	prescr_medica 
		where	nr_prescricao = nr_prescricao_w;
 
		 
		update	prescr_medica      
		set	nr_seq_forma_laudo	= nr_seq_forma_laudo_p, 
			nm_usuario		= nm_usuario_p 
		where	nr_atendimento		= nr_atendimento_p 
		and	nr_prescricao		= nr_prescricao_w;
 
		if (coalesce(ie_atualiza_dt_entrega_w,'N') = 'S') and (nr_seq_forma_laudo_old_w <> nr_seq_forma_laudo_p) then 
		 
			CALL alterar_dt_entrega_prescr(nr_prescricao_w,obter_perfil_ativo,nm_usuario_p);
		 
		end if;
 
		end;
	end loop;
	close C01;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
