CREATE OR REPLACE PROCEDURE atualiza_envio_reserva ( nr_seq_viagem_p bigint, ds_email_destino_p text, ds_observacao_p text, ds_local_p text, nm_usuario_p text) AS $body$
DECLARE


nr_seq_reserva_w	 bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	via_reserva
	where	nr_seq_viagem = nr_seq_viagem_p;


BEGIN


Insert into via_envio(
		NR_SEQUENCIA,
		NR_SEQ_VIAGEM,
		DT_ATUALIZACAO,
		NM_USUARIO,
		DT_ATUALIZACAO_NREC,
		NM_USUARIO_NREC,
		DT_ENVIO,
		IE_TIPO_ENVIO,
		DS_DESTINO,
		DS_OBSERVACAO)
values (nextval('via_envio_seq'),
		nr_seq_viagem_p,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		'E',
		ds_email_destino_p,
		ds_observacao_p);

OPEN C01;
LOOP
FETCH C01 into
	nr_seq_reserva_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	insert into  via_reserva_anexo(NR_SEQUENCIA,
				NR_SEQ_RESERVA,
				DT_ATUALIZACAO,
				NM_USUARIO,
				DT_ATUALIZACAO_NREC,
				NM_USUARIO_NREC,
				DS_ARQUIVO)
		values (nextval('via_reserva_anexo_seq'),
				nr_seq_reserva_w,
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				ds_local_p);
	end;
END LOOP;
CLOSE C01;

commit;

END;
