SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualiza_equipe_regulacao ( nr_seq_reg_p bigint, nr_seq_equipe_p bigint, nm_usuario_p text) AS $body$
DECLARE


ie_possui_resposta_w	varchar(1);
nr_sequencia_w		bigint;
dt_acionamento_w	timestamp;


BEGIN
if (nr_seq_reg_p IS NOT NULL AND nr_seq_reg_p::text <> '') then

	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_possui_resposta_w
	from	eme_reg_resposta
	where	nr_seq_reg = nr_seq_reg_p;

	if (nr_seq_equipe_p IS NOT NULL AND nr_seq_equipe_p::text <> '') then
		if (ie_possui_resposta_w = 'S') then
			select 	max(nr_sequencia)
			into STRICT	nr_sequencia_w
			from	eme_reg_resposta
			where	nr_seq_reg = nr_seq_reg_p
			and	coalesce(nr_seq_equipe::text, '') = '';

			if (nr_sequencia_w IS NOT NULL AND nr_sequencia_w::text <> '') then
				update	eme_reg_resposta
				set	nr_seq_equipe = nr_seq_equipe_p
				where	nr_sequencia = nr_sequencia_w;
			end if;
		else
			select 	max(dt_chamado)
			into STRICT	dt_acionamento_w
			from	eme_regulacao
			where	nr_sequencia = nr_seq_reg_p;

			insert into eme_reg_resposta(	nr_sequencia,
							nr_seq_reg,
							dt_atualizacao,
							nm_usuario,
							nr_seq_equipe,
							dt_atualizacao_nrec,
							nm_usuario_nrec,
							ie_leito_confirmado,
							dt_acionamento)
						values (	nextval('eme_reg_resposta_seq'),
							nr_seq_reg_p,
							clock_timestamp(),
							nm_usuario_p,
							nr_seq_equipe_p,
							clock_timestamp(),
							nm_usuario_p,
							'N',
							dt_acionamento_w);
		end if;
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
