CREATE OR REPLACE PROCEDURE atualiza_espera_pa ( ie_clinica_p bigint, qt_tempo_p bigint, nm_usuario_p text) AS $body$
BEGIN

delete 	from tempo_adic_espera_pa
where	ie_clinica = ie_clinica_p;

commit;


insert into	tempo_adic_espera_pa(nr_sequencia,
		ie_clinica,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		qt_tempo)
		values (nextval('tempo_adic_espera_pa_seq'),
		ie_clinica_p,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		qt_tempo_p);


commit;

end;
