SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualiza_evento_usuario (ie_tipo_evento_p bigint, cd_evento_p bigint, ds_acao_p text, ds_complemento_p text, nm_usuario_p text) AS $body$
DECLARE


dt_agenda_w				timestamp;
dt_agenda_destino_w			timestamp := null;
qt_agenda_w				integer;



BEGIN

if (ie_tipo_evento_p = 0) then
	begin

	select	a.dt_agenda
	into STRICT	dt_agenda_w
	from	agenda_tasy_convite b,
		agenda_tasy a
	where	a.nr_sequencia		= b.nr_seq_agenda
	and	b.nm_usuario_convidado	= nm_usuario_p
	and	a.nr_sequencia		= cd_evento_p;


	select	count(*)
	into STRICT	qt_agenda_w
	from	agenda_tasy
	where	nm_usuario_agenda	= nm_usuario_p
	and	ie_status		= 'M'
	and	dt_agenda		= dt_agenda_w;


	if (qt_agenda_w > 0) and (ds_acao_p = 'S') then
		CALL Wheb_mensagem_pck.exibir_mensagem_abort(279104);
	end if;

	CALL atualizar_agenda_tasy_convite(cd_evento_p,
					dt_agenda_w,
					ds_acao_p,
					ds_complemento_p,
					nm_usuario_p);
	end;
else if (ie_tipo_evento_p = 1) then
	begin
	if (ds_acao_p	= 'C') then
		begin
		update	agenda_tasy
		set	ie_status	= 'C'
		where	nr_sequencia	= cd_evento_p;
		end;
	end if;
	if (ds_acao_p	= 'E') then
		begin
		update	agenda_tasy
		set	ie_status	= 'E'
		where	nr_sequencia	= cd_evento_p;
		end;
	end if;
	if (ds_acao_p	= 'A') then
		begin
		update	agenda_tasy
		set	dt_agenda	= to_date(ds_complemento_p, 'dd/mm/yyyy hh24:mi:ss')
		where	nr_sequencia	= cd_evento_p;
		end;
	end if;
	end;
	end if;
end if;

commit;

end 	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
