CREATE OR REPLACE PROCEDURE atualiza_fator_prognostico ( nr_seq_fator_prog_p bigint, nr_seq_valor_fator_prog_p bigint, nr_seq_can_loco_reg_p bigint, nm_usuario_p text) AS $body$
DECLARE

  nr_sequencia_w bigint;
  qt_fator_w bigint;

BEGIN
	if ((nr_seq_fator_prog_p IS NOT NULL AND nr_seq_fator_prog_p::text <> '') and
		(nr_seq_valor_fator_prog_p IS NOT NULL AND nr_seq_valor_fator_prog_p::text <> '') and 
		(nr_seq_can_loco_reg_p IS NOT NULL AND nr_seq_can_loco_reg_p::text <> '')) then
        
        select count(nr_sequencia)
        into STRICT qt_fator_w
        from fator_prog_loco_reg
        where nr_seq_fator_prog = nr_seq_fator_prog_p
        and nr_seq_can_loco_reg = nr_seq_can_loco_reg_p;

        if (qt_fator_w > 0) then
            delete from fator_prog_loco_reg where nr_seq_fator_prog = nr_seq_fator_prog_p and nr_seq_can_loco_reg = nr_seq_can_loco_reg_p;
        end if;

		select nextval('fator_prog_loco_reg_seq')
		into STRICT nr_sequencia_w
		;
	
		Insert into FATOR_PROG_LOCO_REG(nr_sequencia, dt_atualizacao, nm_usuario, dt_atualizacao_nrec, nm_usuario_nrec, nr_seq_fator_prog, nr_seq_valor_fator_prog, nr_seq_can_loco_reg)
		values (nr_sequencia_w, clock_timestamp(), nm_usuario_p, clock_timestamp(), nm_usuario_p, nr_seq_fator_prog_p, nr_seq_valor_fator_prog_p, nr_seq_can_loco_reg_p);
	end if;

commit;

end;
$body$
