CREATE OR REPLACE PROCEDURE atualiza_filho_regra_agenda ( nr_sequencia_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


lista_agenda_w	varchar(2000);
cd_agenda_w	bigint;

c00 CURSOR FOR
SELECT	cd_agenda
from	agenda_regra
where	nr_seq_agenda_regra = nr_sequencia_p;


BEGIN

open c00;
loop
fetch c00 into
	cd_agenda_w;
EXIT WHEN NOT FOUND; /* apply on c00 */
	if (coalesce(lista_agenda_w::text, '') = '') then
		lista_agenda_w := cd_agenda_w;
	else
		lista_agenda_w := lista_agenda_w||','||cd_agenda_w;
	end if;
end loop;
close c00;
if (lista_agenda_w IS NOT NULL AND lista_agenda_w::text <> '') then
	lista_agenda_w := lista_agenda_w||',';
end if;

delete from agenda_regra where nr_seq_agenda_regra = nr_sequencia_p;
commit;

CALL gerar_regra_agenda_cirurgica(nr_sequencia_p,lista_agenda_w,nm_usuario_p,cd_estabelecimento_p);


end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
