CREATE OR REPLACE PROCEDURE atualiza_fim_adm_qt ( nr_seq_atendimento_p bigint, nm_usuario_p text, dt_fim_adm_p timestamp) AS $body$
DECLARE


cd_estabelecimento_w	integer := wheb_usuario_pck.get_cd_estabelecimento;
nr_seq_estagio_param_w	varchar(20);
qt_existe_estagio_w	integer := 0;
nr_sequencia_autor_w	autorizacao_convenio.nr_sequencia%type;

BEGIN

nr_seq_estagio_param_w := obter_param_usuario(3130, 471, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_w, nr_seq_estagio_param_w);

if (nr_seq_atendimento_p IS NOT NULL AND nr_seq_atendimento_p::text <> '') and (dt_fim_adm_p IS NOT NULL AND dt_fim_adm_p::text <> '') then

	update 	paciente_atendimento
    set 	dt_fim_adm = dt_fim_adm_p,
			nm_usuario = nm_usuario_p
    where 	nr_seq_atendimento =  nr_seq_atendimento_p;

elsif (nr_seq_atendimento_p IS NOT NULL AND nr_seq_atendimento_p::text <> '') and (coalesce(dt_fim_adm_p::text, '') = '') then

	update 	paciente_atendimento
    set 	dt_fim_adm = clock_timestamp(),
			nm_usuario = nm_usuario_p
    where 	nr_seq_atendimento =  nr_seq_atendimento_p;

end if;

if (nr_seq_estagio_param_w IS NOT NULL AND nr_seq_estagio_param_w::text <> '') then

	select	count(*)
	into STRICT	qt_existe_estagio_w
	from	estagio_autorizacao
	where	nr_sequencia = (nr_seq_estagio_param_w)::numeric;

	select	max(a.nr_sequencia)
	into STRICT	nr_sequencia_autor_w
	from	autorizacao_convenio a,
		estagio_autorizacao e
	where	e.nr_sequencia = a.nr_seq_estagio
	and	coalesce(qt_existe_estagio_w,0) > 0
	and	a.nr_seq_paciente = nr_seq_atendimento_p;

	if (nr_sequencia_autor_w IS NOT NULL AND nr_sequencia_autor_w::text <> '') then
		CALL atualizar_autorizacao_convenio(nr_sequencia_autor_w,nm_usuario_p, nr_seq_estagio_param_w,'N','N','S');
	end if;
end if;

if (nr_seq_atendimento_p IS NOT NULL AND nr_seq_atendimento_p::text <> '') then
	CALL integracao_dispensario_pck.cubixx_termino_administracao(nr_seq_atendimento_p);
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
