CREATE OR REPLACE PROCEDURE atualiza_forma_laud_prescr_eup ( nr_atendimento_p bigint, nr_prescricao_p bigint, nr_seq_forma_laudo_p bigint, nm_usuario_p text) AS $body$
DECLARE

nr_seq_forma_laudo_old_w	bigint;
ie_atualiza_dt_entrega_w	varchar(1);


BEGIN 
 
ie_atualiza_dt_entrega_w := Obter_Param_Usuario(916, 645, obter_perfil_ativo, nm_usuario_p, 0, ie_atualiza_dt_entrega_w);
 
 
select	coalesce(max(nr_seq_forma_laudo),0) 
into STRICT	nr_seq_forma_laudo_old_w 
from	prescr_medica 
where	nr_prescricao = nr_prescricao_p;
 
	 
update	prescr_medica      
set	nr_seq_forma_laudo	= nr_seq_forma_laudo_p, 
	nm_usuario		= nm_usuario_p 
where	nr_atendimento		= nr_atendimento_p 
and	nr_prescricao		= nr_prescricao_p;
 
if (coalesce(ie_atualiza_dt_entrega_w,'N') = 'S') and (nr_seq_forma_laudo_old_w <> nr_seq_forma_laudo_p) then 
	 
	CALL alterar_dt_entrega_prescr(nr_prescricao_p,obter_perfil_ativo,nm_usuario_p);
	 
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
