CREATE OR REPLACE PROCEDURE atualiza_forma_pagto_nf ( nr_seq_forma_pagto_p bigint, nr_seq_nota_fiscal_p bigint, nm_usuario_p text) AS $body$
BEGIN
	if (coalesce(nr_seq_nota_fiscal_p,0) > 0) then
		begin
		update  nota_fiscal
		set	nr_seq_forma_pagto = nr_seq_forma_pagto_p,
			dt_atualizacao = clock_timestamp(),
			nm_usuario = nm_usuario_p
		where	nr_sequencia = nr_seq_nota_fiscal_p;
		commit;
		end;
	end if;

end;
$body$
