CREATE OR REPLACE PROCEDURE atualiza_hist_lote_pagar ( nm_usuario_p text, ds_historico_p text, nr_titulo_p bigint, ie_tipo_historico_p text) AS $body$
DECLARE


nr_seq_historico_w	tipo_hist_tit_pagar.nr_sequencia%type;


BEGIN
if (ie_tipo_historico_p IS NOT NULL AND ie_tipo_historico_p::text <> '') then
	select	max(a.nr_sequencia)
	into STRICT	nr_seq_historico_w
	from	tipo_hist_tit_pagar a
	where	a.ie_tipo_historico = ie_tipo_historico_p;
end if;
if (nr_titulo_p IS NOT NULL AND nr_titulo_p::text <> '') then
	begin
	insert into titulo_pagar_hist(
		NR_SEQUENCIA,
		DT_ATUALIZACAO,
		NM_USUARIO,
		DT_ATUALIZACAO_NREC,
		NM_USUARIO_NREC,
		NR_TITULO,
		DT_HISTORICO,
		NR_SEQ_HISTORICO,
		DS_HISTORICO)
	values (	nextval('titulo_pagar_hist_seq'),
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_titulo_p,
		clock_timestamp(),
		nr_seq_historico_w,
		ds_historico_p);
	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
