CREATE OR REPLACE PROCEDURE atualiza_impressao_rep ( nr_prescricao_p bigint, nm_tabela_p text, ie_opcao_p text) AS $body$
BEGIN
if (nm_tabela_p = 'PRESCR_MEDICA') then
	begin
	if (ie_opcao_p = 'P') then
		update	prescr_medica
		set		dt_impressao 	= clock_timestamp()
		where	nr_prescricao 	= nr_prescricao_p;
	elsif (ie_opcao_p = 'EX') then
		update	prescr_medica
		set		dt_impressao 		= clock_timestamp(),
				dt_impressao_exame	= clock_timestamp()
		where	nr_prescricao 		= nr_prescricao_p;
	end if;
	end;
end if;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
