CREATE OR REPLACE PROCEDURE atualiza_info_preco_venda (cd_tab_preco_mat_p bigint, cd_material_p bigint, cd_estabelecimento_p bigint, dt_inicio_vigencia_p timestamp, nr_seq_motivo_p bigint, ds_justificativa_p text, nm_usuario_p text) AS $body$
DECLARE


nr_seq_motivo_w	bigint;


BEGIN

if (nr_seq_motivo_p <> 0) then
	nr_seq_motivo_w	:= nr_seq_motivo_p;
else
	nr_seq_motivo_w	:= null;
end if;

Update 	preco_material
set 	ie_preco_venda = CASE WHEN coalesce(ie_preco_venda,'S')='S' THEN  'N'  ELSE 'S' END ,
	ie_manual      = 'S',
	ie_inpart = CASE WHEN ie_inpart='A' THEN  'S'  ELSE ie_inpart END ,
	nr_seq_motivo  = nr_seq_motivo_p
where 	cd_tab_preco_mat = cd_tab_preco_mat_p
and   	cd_estabelecimento = cd_estabelecimento_p
and   	cd_material        = cd_material_p
and   	dt_inicio_vigencia = dt_inicio_vigencia_p;

commit;

CALL atualiza_preco_mat_justif(cd_tab_preco_mat_p, cd_material_p, cd_estabelecimento_p, dt_inicio_vigencia_p, nr_seq_motivo_p, ds_justificativa_p, nm_usuario_p);

CALL Atualiza_Registro_Atual_Inpart(cd_tab_preco_mat_p, cd_estabelecimento_p, cd_material_p, nm_usuario_p);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
