CREATE OR REPLACE PROCEDURE atualiza_inicio_analise_farm ( nr_prescricao_p bigint, ds_justificativa_p text, nm_usuario_p text) AS $body$
DECLARE

ie_inicia_w			varchar(1):='N';													
nm_usuario_w			varchar(15);
nm_pessoa_fisica_w 		varchar(255);
dt_inicio_analise_farm_w	timestamp;
													

BEGIN 
 
/* 
select	decode(count(*),0,'S','N') 
into	ie_inicia_w 
from	prescr_medica 
where	nr_prescricao = nr_prescricao_p 
and	dt_inicio_analise_farm is not null; 
*/
 
 
--if	(ie_inicia_w = 'N') then 
select	nm_usuario_analise_farm, 
	obter_nome_pf(obter_dados_usuario_opcao(nm_usuario_analise_farm,'C')), 
	dt_inicio_analise_farm, 
	CASE WHEN coalesce(dt_inicio_analise_farm::text, '') = '' THEN 'S'  ELSE 'N' END  
into STRICT	nm_usuario_w, 
	nm_pessoa_fisica_w, 
	dt_inicio_analise_farm_w, 
	ie_inicia_w 
from	prescr_medica 
where	nr_prescricao = nr_prescricao_p;
--end if;	 
 
update	prescr_medica 
set	dt_inicio_analise_farm 	= CASE WHEN ie_inicia_w='S' THEN clock_timestamp()  ELSE null END , 
	nm_usuario_analise_farm = CASE WHEN ie_inicia_w='S' THEN nm_usuario_p  ELSE null END  
where	nr_prescricao 		= nr_prescricao_p;
 
insert	into	log_analise_prescr( 
		nr_sequencia, 
		dt_atualizacao, 
		nm_usuario, 
		nr_prescricao, 
		ie_acao, 
		dt_acao, 
		nm_usuario_acao, 
		ds_motivo_acao) 
values (nextval('log_analise_prescr_seq'), 
		clock_timestamp(), 
		nm_usuario_p, 
		nr_prescricao_p, 
		CASE WHEN ie_inicia_w='S' THEN 'I'  ELSE 'D' END , 
		clock_timestamp(), 
		nm_usuario_p, 
		ds_justificativa_p);
 
commit;
 
end	;
