CREATE OR REPLACE PROCEDURE atualiza_inicio_analise_gt ( nr_sequencia_p bigint, ds_justificativa_p text, nm_usuario_p text) AS $body$
DECLARE

 
ie_inicia_w			varchar(1):='N';													
nm_usuario_w			varchar(15);
nm_pessoa_fisica_w 		varchar(255);
dt_inicio_analise_transcr_w	timestamp;
													

BEGIN 
 
select	CASE WHEN count(*)=0 THEN 'S'  ELSE 'N' END  
into STRICT	ie_inicia_w 
from	transcricao_prescricao 
where	nr_sequencia = nr_sequencia_p 
and	(dt_inicio_analise_transcr IS NOT NULL AND dt_inicio_analise_transcr::text <> '');
 
if (ie_inicia_w = 'N') then 
	select	nm_usuario_analise_transcr, 
		obter_nome_pf(obter_dados_usuario_opcao(nm_usuario_analise_transcr,'C')), 
		dt_inicio_analise_transcr 
	into STRICT	nm_usuario_w, 
		nm_pessoa_fisica_w, 
		dt_inicio_analise_transcr_w 
	from	transcricao_prescricao 
	where	nr_sequencia = nr_sequencia_p;
end if;	
 
update	transcricao_prescricao 
set	dt_inicio_analise_transcr	= CASE WHEN ie_inicia_w='S' THEN clock_timestamp()  ELSE null END , 
	nm_usuario_analise_transcr 	= CASE WHEN ie_inicia_w='S' THEN nm_usuario_p  ELSE null END  
where	nr_sequencia			= nr_sequencia_p;
 
insert	into	log_analise_prescr( 
		nr_sequencia, 
		dt_atualizacao, 
		nm_usuario, 
		nr_prescricao, 
		ie_acao, 
		dt_acao, 
		nm_usuario_acao, 
		ds_motivo_acao, 
		nr_seq_transcricao) 
values (nextval('log_analise_prescr_seq'), 
		clock_timestamp(), 
		nm_usuario_p, 
		null, 
		CASE WHEN ie_inicia_w='S' THEN 'I'  ELSE 'D' END , 
		clock_timestamp(), 
		nm_usuario_p, 
		ds_justificativa_p, 
		nr_sequencia_p);
 
commit;
 
end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
