CREATE OR REPLACE PROCEDURE atualiza_inspecao_rec_contagem ( ds_barras_p text, qt_inspecao_p bigint, nr_sequencia_p text, nm_usuario_p text) AS $body$
DECLARE

 
qt_inspecao_w			bigint;					

BEGIN 
 
select coalesce(qt_inspecao, 0) 
into STRICT qt_inspecao_w 
from inspecao_rec_contagem 
where nr_sequencia = nr_sequencia_p;
 
qt_inspecao_w := qt_inspecao_w + qt_inspecao_p;
 
update inspecao_rec_contagem 
set ds_barras = ds_barras_p, qt_inspecao = qt_inspecao_w 
where nr_sequencia = nr_sequencia_p;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
