CREATE OR REPLACE PROCEDURE atualiza_item_achado_vencido ( cd_estabelecimento_p text, nm_usuario_p text) AS $body$
DECLARE


nr_seq_item_w	bigint;
nr_seq_status_job_w	bigint;

C01 CURSOR FOR
	SELECT	a.nr_sequencia
	from	atend_achado_perdido_item a,
		classif_item_achado_perd b
	where	coalesce(a.dt_baixa::text, '') = ''
	and	b.nr_sequencia = a.nr_seq_classif_item
	and	((trunc(a.dt_atualizacao_nrec) + b.qt_dias_validade) = (trunc(clock_timestamp())-1));


BEGIN

select	coalesce(max(nr_seq_status_job),0)
into STRICT	nr_seq_status_job_w
from	parametro_achado_perdido
where	cd_estabelecimento = cd_estabelecimento_p;

open C01;
loop
fetch C01 into
	nr_seq_item_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

		update	atend_achado_perdido_item
		set	nm_usuario = nm_usuario_p,
			nr_seq_status_achado_perdido = nr_seq_status_job_w
		where	nr_sequencia = nr_seq_item_w;

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
