CREATE OR REPLACE PROCEDURE atualiza_justif_hemocomponente (nr_prescricao_p bigint, nr_seq_proced_p bigint, nr_seq_justif_p bigint, nm_usuario_p text) AS $body$
DECLARE


ds_justificativa_w	varchar(2000);


BEGIN

select	max(ds_justificativa)
into STRICT	ds_justificativa_w
from	rep_hemocomponente_justif
where	nr_sequencia	= nr_seq_justif_p;

if (ds_justificativa_w IS NOT NULL AND ds_justificativa_w::text <> '') then
	update	prescr_procedimento
	set	ds_justificativa = ds_justificativa_w
	where	nr_prescricao	= nr_prescricao_p
	and	nr_sequencia	= nr_seq_proced_p;

	commit;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
