CREATE OR REPLACE PROCEDURE atualiza_justif_res_sexo_dif ( nr_seq_unidade_p bigint, nm_usuario_p text, ds_motivo_reserva_p text) AS $body$
DECLARE


nr_seq_hist_w		bigint;



BEGIN

if (coalesce(nr_seq_unidade_p,0) > 0) then

	select	coalesce(max(nr_sequencia),0)
	into STRICT	nr_seq_hist_w
	from  	unidade_atend_hist
	where	nr_seq_unidade    = nr_seq_unidade_p
	and	ie_status_unidade = 'R'
	and	coalesce(dt_fim_historico::text, '') = '';

	if (nr_seq_hist_w > 0) then

		update	unidade_atend_hist
		set	dt_atualizacao     = clock_timestamp(),
			nm_usuario         = nm_usuario_p,
			ds_motivo_reserva  = ds_motivo_reserva_p,
			nm_usuario_reserva = nm_usuario_p
		where	nr_sequencia       = nr_seq_hist_w;

	end if;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
