CREATE OR REPLACE PROCEDURE atualiza_just_excesso_serv ( nr_seq_unid_p bigint, ds_motivo_p text, nm_usuario_p text) AS $body$
BEGIN
if (coalesce(nr_seq_unid_p,0) > 0) and (ds_motivo_p IS NOT NULL AND ds_motivo_p::text <> '')  then

	update	sl_unid_atend
	set	ds_just_tempo_serv	= ds_motivo_p,
		dt_atualizacao		= clock_timestamp(),
		nm_usuario		= nm_usuario_p
	where	nr_sequencia 		= nr_seq_unid_p;

end if;
commit;

end;
$body$
LANGUAGE PLPGSQL
