SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualiza_just_orcamento ( nm_usuario_p text, cd_classificacao_p text, ds_justificativa_p text) AS $body$
DECLARE


qt_registros_w	bigint;


BEGIN

select	count(*)
into STRICT	qt_registros_w
from	w_ctb_acomp_orcamento
where	nm_usuario = nm_usuario_p
and	cd_classificacao = cd_classificacao_p;

if (qt_registros_w > 0) then

	update	w_ctb_acomp_orcamento
	set	ds_justificativa = ds_justificativa_p
	where	nm_usuario = nm_usuario_p
	and	cd_classificacao = cd_classificacao_p;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
