CREATE OR REPLACE PROCEDURE atualiza_just_retro_evento ( nr_seq_evento_p bigint, ds_justificativa_retro_p text ) AS $body$
BEGIN
    IF (nr_seq_evento_p IS NOT NULL AND nr_seq_evento_p::text <> '' AND ds_justificativa_retro_p IS NOT NULL AND ds_justificativa_retro_p::text <> '') THEN
        UPDATE  evento_cirurgia_paciente
        SET     ds_justificativa_retro = ds_justificativa_retro_p
        WHERE   nr_sequencia = nr_seq_evento_p;

        COMMIT;
    END IF;
END;
