CREATE OR REPLACE PROCEDURE atualiza_liberacao_acesso ( ie_tipo_visitante_p text, nr_seq_visitante_p bigint, nr_atendimento_p bigint, nr_Acompanhante_p bigint, nm_usuario_p text) AS $body$
BEGIN

if (ie_tipo_visitante_p	= 'A') then
	update	atendimento_acompanhante
	set	dt_liberacao_acesso	= clock_timestamp()
	where	nr_atendimento		= nr_Atendimento_p
	and	nr_acompanhante		= nr_acompanhante_p;

elsif (ie_tipo_visitante_p	= 'ACP') then
	update	acompanhante_paciente
	set	dt_liberacao_acesso	= clock_timestamp()
	where	nr_sequencia		= nr_seq_visitante_p;

else
	update	atendimento_visita
	set	dt_liberacao_acesso	= clock_timestamp()
	where	nr_sequencia		= nr_seq_visitante_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
