CREATE OR REPLACE PROCEDURE atualiza_liberacao_anestesia (nm_usuario_p text, nr_cirurgia_p bigint, nr_seq_pepo_p bigint) AS $body$
BEGIN

if (nr_cirurgia_p IS NOT NULL AND nr_cirurgia_p::text <> '')
or (nr_seq_pepo_p IS NOT NULL AND nr_seq_pepo_p::text <> '') then

	update	cirurgia_tec_anestesica
	set	dt_liberacao	=	clock_timestamp(),
	dt_atualizacao		=	clock_timestamp(),
                nm_usuario		=	nm_usuario_p
	where	((nr_cirurgia	=  nr_cirurgia_p)     or (coalesce(nr_cirurgia_p::text, '') = ''))
	and	((nr_seq_pepo 	=  nr_seq_pepo_p) or (coalesce(nr_seq_pepo_p::text, '') = ''))
	and	coalesce(dt_liberacao::text, '') = '';

	update	anestesia_descricao
	set	 dt_liberacao     	= clock_timestamp(),
		 dt_atualizacao 	= clock_timestamp(),
		nm_usuario	= nm_usuario_p
	where	((nr_cirurgia 	= nr_cirurgia_p)     or (coalesce(nr_cirurgia_p::text, '') = ''))
	and	((nr_seq_pepo 	= nr_seq_pepo_p) or (coalesce(nr_seq_pepo_p::text, '') = ''))
	and	coalesce(dt_liberacao::text, '') = '';

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
