CREATE OR REPLACE PROCEDURE atualiza_lib_contas_pagar ( nr_titulo_p bigint, cd_perfil_p bigint, nm_usuario_p text) AS $body$
BEGIN

if (nr_titulo_p IS NOT NULL AND nr_titulo_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') and (cd_perfil_p IS NOT NULL AND cd_perfil_p::text <> '') then

	update	conta_pagar_lib a
	set	dt_liberacao	= CASE WHEN dt_liberacao = NULL THEN  clock_timestamp()  ELSE null END ,
		dt_atualizacao	= clock_timestamp(),
		nm_usuario	= nm_usuario_p
	where	a.ie_nivel	= (	SELECT	min(x.ie_nivel)
					from	conta_pagar_lib x
					where	coalesce(x.dt_liberacao::text, '') = ''
					and (x.nm_usuario_lib	= nm_usuario_p or x.cd_perfil	= cd_perfil_p)
					and	x.nr_titulo		= nr_titulo_p)
	and (nm_usuario_lib	= nm_usuario_p or cd_perfil	= cd_perfil_p)
	and	nr_titulo	= nr_titulo_p;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
