CREATE OR REPLACE PROCEDURE atualiza_lib_parametro_sistema ( cd_funcao_p bigint, nr_sequencia_p bigint, nm_usuario_lib_p text, dt_liberacao_p timestamp) AS $body$
BEGIN
if (cd_funcao_p IS NOT NULL AND cd_funcao_p::text <> '') and (nr_sequencia_p > 0) then
	update	funcao_parametro
	set	nm_usuario_lib 	= nm_usuario_lib_p,
		dt_liberacao	= dt_liberacao_p,
		dt_atualizacao	= clock_timestamp()
	where	cd_funcao 	= cd_funcao_p
	and	nr_sequencia	= nr_sequencia_p;

	commit;
end if;

end;
$body$
