CREATE OR REPLACE PROCEDURE atualiza_lotes_adep_processo (nr_lote_devolvido_p text, nr_lote_preparacao_p text, nr_seq_frac_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


ie_permite_registrar_w	varchar(15);
ie_permite_w		varchar(1);


BEGIN

ie_permite_registrar_w := obter_param_usuario(3112, 22, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_permite_registrar_w);

if (ie_permite_registrar_w = 'N') then
	select	coalesce(max('S'),'N')
	into STRICT	ie_permite_w
	from	material_lote_fornec
	where	ds_lote_fornec = to_char(nr_lote_preparacao_p);

	if (ie_permite_w = 'N') then
		--Lote de preparação inválido!#@#@');
		CALL Wheb_mensagem_pck.exibir_mensagem_abort(261403);
	end if;
end if;

if (nr_seq_frac_p IS NOT NULL AND nr_seq_frac_p::text <> '') then

	update 	adep_processo_item
	set	nr_lote_devolvido = nr_lote_devolvido_p,
		nr_lote_preparacao = nr_lote_preparacao_p
	where	nr_sequencia = nr_seq_frac_p;

	commit;
end if;

end;
$body$
