CREATE OR REPLACE PROCEDURE atualiza_lote_gerado ( ds_arquivo_p text, nr_seq_lote_p bigint, nm_usuario_p text, ie_tipo_p text) AS $body$
DECLARE


ds_historico_w		varchar(255);


BEGIN

update	lote_orgao_cobranca
set	dt_remessa_retorno = clock_timestamp()
where	nr_sequencia = nr_seq_lote_p;

if (ie_tipo_p = 'E') then

	update	lote_orgao_cobranca
	set	ds_arquivo = ds_arquivo_p
	where	nr_sequencia = nr_seq_lote_p;

	update	lote_orgao_cobranca
	set	ie_situacao_lote = 'E'
	where	nr_sequencia = nr_seq_lote_p;

	ds_historico_w := wheb_mensagem_pck.get_texto(338318, 'NM_USUARIO=' || nm_usuario_p || ';DS_ARQUIVO=' || ds_arquivo_p || ';DT_ATUALIZACAO=' || to_char(clock_timestamp(),'dd/MM/yyyy hh24:mi:ss'));
	CALL gerar_historico_cobr_orgao(null,null,nr_seq_lote_p,ds_historico_w,nm_usuario_p,'E');

elsif (ie_tipo_p = 'R') then

	update	lote_orgao_cobranca
	set	ie_situacao_lote = 'R'
	where	nr_sequencia = nr_seq_lote_p;

	ds_historico_w := wheb_mensagem_pck.get_texto(338555, 'NM_USUARIO=' || nm_usuario_p || ';DS_ARQUIVO=' || ds_arquivo_p || ';DT_ATUALIZACAO=' || to_char(clock_timestamp(),'dd/MM/yyyy hh24:mi:ss'));
	CALL gerar_historico_cobr_orgao(null,null,nr_seq_lote_p,ds_historico_w,nm_usuario_p,'E');

end if;

commit;

end;
