SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualiza_material_barras_nota ( nr_sequencia_p bigint, nr_item_nf_p bigint, nm_usuario_p text) AS $body$
DECLARE



cd_cgc_w			varchar(14);
cd_material_w			integer;
cd_barra_material_w		varchar(255);
cd_barras_w			varchar(40);


c01 CURSOR FOR
SELECT	a.cd_material,
	b.cd_barras
from	nota_fiscal_item a,
	w_nota_fiscal_item_barra b
where	a.nr_sequencia	= nr_sequencia_p
and	a.nr_item_nf	= nr_item_nf_p
and	a.nr_sequencia	= b.nr_seq_nota
and	a.nr_item_nf	= b.nr_seq_item
order by 1;


BEGIN

select	cd_cgc
into STRICT	cd_cgc_w
from	nota_fiscal
where	nr_sequencia = nr_sequencia_p;

open c01;
loop
	fetch c01 into
		cd_material_w,
		cd_barras_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */

	begin
	if (cd_barras_w IS NOT NULL AND cd_barras_w::text <> '') then
		begin

		select	coalesce(max(cd_barra_material),'X')
		into STRICT	cd_barra_material_w
		from	material_cod_barra
		where	cd_barra_material = cd_barras_w;

		if (cd_barra_material_w = 'X') then
			insert into material_cod_barra(
				cd_material,
				cd_barra_material,
				dt_atualizacao,
				nm_usuario,
				cd_cgc_fabricante,
				ds_observacao,
				dt_atualizacao_nrec,
				nm_usuario_nrec)
			values (	cd_material_w,
				cd_barras_w,
				clock_timestamp(),
				nm_usuario_p,
				cd_cgc_w,
				null,
				clock_timestamp(),
				nm_usuario_p);
		end if;
		end;
	end if;
	end;
end loop;
close c01;

commit;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
