SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualiza_media_bytes (USUARIO_P text) AS $body$
DECLARE


nm_tabela_w		varchar(30);
qt_media_bytes_w	bigint;

C010 CURSOR FOR
SELECT nm_tabela
FROM tabela_sistema;


BEGIN

OPEN C010;
LOOP
	FETCH C010 into	nm_tabela_w;
	if  C010%FOUND then
            	begin
		select sum(coalesce(qt_tamanho_calculo,
				CASE WHEN 				IE_TIPO_ATRIBUTO='DATA' THEN  7 WHEN 				IE_TIPO_ATRIBUTO='LONG' THEN  QT_TAMANHO / 2  ELSE QT_TAMANHO END ))
		into STRICT qt_media_bytes_w
     		from tabela_atributo
     		where nm_tabela = nm_tabela_w;

		update tabela_sistema
		set qt_media_bytes 	= qt_media_bytes_w,
		    dt_atualizacao	= clock_timestamp(),
		    nm_usuario		= usuario_p
		where nm_tabela 	= nm_tabela_w;
		end;
	else
		exit;
	end if;
END LOOP;
CLOSE C010;
COMMIT;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE atualiza_media_bytes (USUARIO_P text) FROM PUBLIC;

