CREATE OR REPLACE PROCEDURE atualiza_medico_ciente ( nr_atendimento_p bigint, cd_medico_p text, ie_forma_aviso_p text, nm_usuario_p text) AS $body$
DECLARE

 
/* 
  Forma de aviso 
 M - Médico anterior 
 P - Próprio médico 
 T - Telefone 
*/
 
 
cd_medico_resp_w	varchar(10);
cd_setor_atendimento_w	integer;
ie_tipo_atendimento_w	smallint;


BEGIN 
if (nr_atendimento_p	> 0) then 
	select	max(obter_setor_atendimento(nr_atendimento_p)) 
	into STRICT	cd_setor_atendimento_w 
	;
end if;
 
Select	max(cd_medico_resp), 
	max(ie_tipo_atendimento) 
into STRICT	cd_medico_resp_w, 
	ie_tipo_atendimento_w 
from	atendimento_paciente 
where	nr_atendimento = nr_atendimento_p;
 
update	atendimento_troca_medico 
set	dt_ciente 		= clock_timestamp(), 
	dt_atualizacao		= clock_timestamp(), 
	cd_setor_atendimento	= cd_setor_atendimento_w, 
	ie_tipo_atendimento	= ie_tipo_atendimento_w, 
	ie_forma_aviso		= ie_forma_aviso_p, 
	nm_usuario		= nm_usuario_p, 
	nm_usuario_ciente	= nm_usuario_p 
where	nr_atendimento 		= nr_atendimento_p 
and	coalesce(dt_ciente::text, '') = '' 
and	cd_medico_atual 	= cd_medico_p 
and	cd_medico_resp_w	= cd_medico_p;
 
commit;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
