SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualiza_min_escala_diaria ( nm_usuario_p text, nr_seq_escala_p bigint, ie_escala_p text, ie_opcao_p text) AS $body$
DECLARE


dt_inicio_w		timestamp;
dt_fim_w			timestamp;


/*
ie_opcao
C = Confirmar escala
D = Desfazer confirmação escala

ie_escala
ED   = escala diaria
EDA = escala diaria adic
*/
BEGIN
if (nr_seq_escala_p IS NOT NULL AND nr_seq_escala_p::text <> '') then
	if (ie_escala_p = 'ED') then
		if (ie_opcao_p = 'C') then

			update	escala_diaria
			set	nm_usuario = nm_usuario_p,
				qt_min_executado =	(to_date(to_char(dt_fim,'dd/mm/yyyy hh24:mi'),'dd/mm/yyyy hh24:mi:ss') -
							to_date(to_char(dt_inicio,'dd/mm/yyyy hh24:mi'),'dd/mm/yyyy hh24:mi:ss')) * 1440
			where	nr_sequencia = nr_seq_escala_p;

		elsif (ie_opcao_p = 'D') then

			update	escala_diaria
			set	nm_usuario = nm_usuario_p,
				qt_min_executado  = NULL
			where	nr_sequencia = nr_seq_escala_p;

		end if;
	elsif (ie_escala_p = 'EDA') then
		if (ie_opcao_p = 'C') then

			select	a.dt_inicio,
				a.dt_fim
			into STRICT	dt_inicio_w,
				dt_fim_w
			from	escala_diaria_adic b,
				escala_diaria a
			where	a.nr_sequencia = b.nr_seq_escala_diaria
			and	b.nr_sequencia = nr_seq_escala_p;

			update	escala_diaria_adic
			set	nm_usuario = nm_usuario_p,
				qt_min_executado =	(to_date(to_char(dt_fim_w,'dd/mm/yyyy hh24:mi'),'dd/mm/yyyy hh24:mi:ss') -
							to_date(to_char(dt_inicio_w,'dd/mm/yyyy hh24:mi'),'dd/mm/yyyy hh24:mi:ss')) * 1440
			where	nr_sequencia = nr_seq_escala_p;
		elsif (ie_opcao_p = 'D') then

			update	escala_diaria_adic
			set	nm_usuario = nm_usuario_p,
				qt_min_executado  = NULL
			where	nr_sequencia = nr_seq_escala_p;
		end if;
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
