CREATE OR REPLACE PROCEDURE atualiza_motivo_conv_ret_glosa ( cd_motivo_glosa_p bigint, cd_setor_responsavel_p bigint, cd_resposta_p bigint, nr_sequencia_p bigint, nm_usuario_p text, ie_acao_glosa_p text) AS $body$
DECLARE


ie_acao_glosa_w		varchar(10);
cd_setor_responsavel_w	integer;
cd_resposta_w		bigint;


BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then

begin

	if (coalesce(cd_setor_responsavel_p::text, '') = '') then
		select	cd_setor_responsavel
		into STRICT	cd_setor_responsavel_w
		from	convenio_retorno_glosa
		where	nr_sequencia		= nr_sequencia_p;
	else
		cd_setor_responsavel_w	:= cd_setor_responsavel_p;
	end if;

	if (coalesce(cd_resposta_p::text, '') = '') then
		select	cd_resposta
		into STRICT	cd_resposta_w
		from	convenio_retorno_glosa
		where	nr_sequencia		= nr_sequencia_p;
	else
		cd_resposta_w	:= cd_resposta_p;
	end if;

	if (coalesce(ie_acao_glosa_p,'N') = 'S') then
		select	ie_acao_glosa
		into STRICT	ie_acao_glosa_w
		from	motivo_glosa
		where	cd_motivo_glosa		= cd_motivo_glosa_p;
	end if;

	update	convenio_retorno_glosa
	set	cd_motivo_glosa		= cd_motivo_glosa_p,
		nm_usuario		= nm_usuario_p,
		cd_setor_responsavel	= cd_setor_responsavel_w,
		cd_resposta		= cd_resposta_w,
		dt_atualizacao		= clock_timestamp(),
		ie_acao_glosa		= coalesce(ie_acao_glosa_w,ie_acao_glosa)
	where	nr_sequencia		= nr_sequencia_p;

	commit;

end;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
