SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualiza_motivo_glosa ( nr_seq_ret_item_p bigint, nm_usuario_p text, ie_commit_p text) AS $body$
DECLARE


/*	Atualiza motivo da glosa conforme
	"Motivo Glosa", do DPB CONVENIO_RETORNO_ITEM */
cd_motivo_glosa_w	integer;


BEGIN

select 	a.cd_motivo_glosa
into STRICT	cd_motivo_glosa_w
from	convenio_retorno_item a
where	a.nr_sequencia		= nr_seq_ret_item_p;

if (cd_motivo_glosa_w IS NOT NULL AND cd_motivo_glosa_w::text <> '') then

	update	convenio_retorno_glosa
	set	dt_atualizacao	= clock_timestamp(),
		nm_usuario	= nm_usuario_p,
		cd_motivo_glosa	= cd_motivo_glosa_w
	where	nr_seq_ret_item	= nr_seq_ret_item_p
	and	coalesce(cd_motivo_glosa::text, '') = '';

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
