CREATE OR REPLACE PROCEDURE atualiza_mot_desbloqueio_acomp ( nm_usuario_p text, nr_atendimento_p bigint, nr_seq_motivo_desb_p bigint) AS $body$
DECLARE

nr_sequencia_w	bigint;


BEGIN

if (coalesce(nr_atendimento_p,0) > 0) and (coalesce(nr_seq_motivo_desb_p,0) > 0) then

	select 	nextval('atendimento_historico_seq')
	into STRICT	nr_sequencia_w
	;

	insert into atendimento_historico(	nr_sequencia,
						dt_atualizacao,
						nm_usuario,
						dt_atualizacao_nrec,
						nm_usuario_nrec,
						nr_atendimento,
						ds_historico,
						nr_seq_motivo_lib,
						nm_usuario_motivo_lib,
						dt_liberacao)
				values (		nr_sequencia_w,
						clock_timestamp(),
						nm_usuario_p,
						clock_timestamp(),
						nm_usuario_p,
						nr_atendimento_p,
						obter_desc_expressao(730797), --Liberação de acompanhantes para o atendimento
						nr_seq_motivo_desb_p,
						nm_usuario_p,
						clock_timestamp());


end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
