CREATE OR REPLACE PROCEDURE atualiza_nfse_bragancapaulista ( nm_usuario_p text, cd_verificacao_nfse_p text, nr_nfse_p text, cd_estabelecimento_p bigint, nr_sequencia_p bigint) AS $body$
BEGIN

if (nr_sequencia_p > 0) then

	update	nota_fiscal
	set	nr_nfe_imp 		= nr_nfse_p,
		cd_verificacao_nfse	= cd_verificacao_nfse_p
	where	nr_sequencia 		= nr_sequencia_p;

	update	nota_fiscal_item
	set	nr_nfe_imp 	= nr_nfse_p
	where	nr_sequencia 	= nr_sequencia_p;

	insert into nota_fiscal_hist(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_seq_nota,
		cd_evento,
		ds_historico)
	values (	nextval('nota_fiscal_hist_seq'),
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_sequencia_p,
		'27',
		'NFS-e: ' || nr_nfse_p);

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
