SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualiza_nota_alteracao (nr_seq_alteracao_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_nota_requisito_w	double precision;
qt_nota_final_w		double precision;


BEGIN

select	coalesce(sum(a.qt_pontos), 0)
into STRICT	qt_nota_requisito_w
from	requisito_alteracao a,
	alteracao_tasy_req b
where	b.nr_seq_alteracao	= nr_seq_alteracao_p
and	a.nr_sequencia		= b.nr_seq_requisito;

qt_nota_final_w		:= (10 - coalesce(qt_nota_requisito_w, 0));

if (qt_nota_final_w < 0) then
	qt_nota_final_w	:= 0;
end if;

update	alteracao_tasy
set	qt_nota		= qt_nota_final_w,
	nm_usuario	= nm_usuario_p,
	dt_atualizacao	= clock_timestamp()
where	nr_sequencia	= nr_seq_alteracao_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
