SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualiza_nota_interface ( nm_usuario_p text, ds_arquivo_p text, cd_interface_txt_p bigint, cd_interface_xml_p bigint ) AS $body$
DECLARE



nr_seq_nota_fiscal_w		bigint;
nr_seq_lote_w			bigint;
nr_sequencia_w			bigint;

c01 CURSOR FOR
	SELECT	nr_seq_nota_fiscal,
		nr_seq_lote
	from	w_nota_fiscal;


BEGIN

select	max(a.nr_seq_lote)
into STRICT	nr_sequencia_w
from	w_nota_fiscal a
where not exists (
	SELECT	1
	from	nota_fiscal_exportada x
	where	x.nr_sequencia = a.nr_seq_lote)
and a.nm_usuario = nm_usuario_p;

if (coalesce(nr_sequencia_w,0) = 0) then
	select	nextval('nota_fiscal_exportada_seq')
	into STRICT	nr_sequencia_w
	;
end if;

insert into nota_fiscal_exportada(
	nr_sequencia,
	dt_atualizacao,
	nm_usuario,
	dt_atualizacao_nrec,
	nm_usuario_nrec,
	ds_arquivo,
	cd_interface_txt,
	cd_interface_xml)
values (	nr_sequencia_w,
	clock_timestamp(),
	nm_usuario_p,
	clock_timestamp(),
	nm_usuario_p,
	substr(ds_arquivo_p,1,80),
	cd_interface_txt_p,
	cd_interface_xml_p);

open C01;
loop
fetch	C01 into
	nr_seq_nota_fiscal_w,
	nr_seq_lote_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	update	nota_fiscal_exportada
	set	nr_sequencia = nr_seq_lote_w
	where	nr_sequencia = nr_sequencia_w;

	update	nota_fiscal
	set	ie_status_envio = 'E',
		nr_seq_exportada = nr_seq_lote_w
	where	nr_sequencia = nr_seq_nota_fiscal_w;

	insert into nota_fiscal_hist(
			nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			nr_seq_nota,
			cd_evento,
			ds_historico)
		values (	nextval('nota_fiscal_hist_seq'),
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			nr_seq_nota_fiscal_w,
			'21',
			substr(wheb_mensagem_pck.get_texto(312254)|| ': ' || ds_arquivo_p,1,255));
	end;
end loop;
close c01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
