CREATE OR REPLACE PROCEDURE atualiza_objeto_sistema_hist ( NM_OBJETO_P text, NM_USUARIO_P text, DS_SCRIPT_CRIACAO_P text, NR_ORDEM_SERVICO_P bigint) AS $body$
DECLARE


nr_sequencia_w	bigint;
nr_sequencia_ww	bigint;
NM_OBJETO_W	varchar(50);
DT_ATUALIZACAO_W	timestamp;
NM_USUARIO_W	varchar(15);
DS_SCRIPT_CRIACAO_W	text;
NR_ORDEM_SERVICO_W	bigint;


C01 CURSOR FOR
	SELECT	nr_sequencia
	from	objeto_sistema_hist
	where	trunc(dt_atualizacao)	= trunc(clock_timestamp())
	and	nm_objeto	= nm_objeto_p
	and	nm_usuario = nm_usuario_p
	and	nr_ordem_servico = nr_ordem_servico_p
	and	nr_sequencia = nr_sequencia_w;


BEGIN

select max(nr_sequencia)
into STRICT nr_sequencia_w
  from objeto_sistema_hist
 where nm_objeto = nm_objeto_p;

  open C01;
  loop
  fetch C01
  into nr_sequencia_ww;
  EXIT WHEN NOT FOUND; /* apply on C01 */
    begin

	update	 objeto_sistema_hist
	set	dt_atualizacao = clock_timestamp(),
		ds_script_criacao = ds_script_criacao_p
	where	nr_sequencia = nr_sequencia_ww
	and	nm_objeto = nm_objeto_p;

	commit;

    end;
    end loop;
    close C01;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
