SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualiza_obs_agendamento (nr_seq_agenda_p bigint, ds_observacao_p text, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


ds_observacao_w		varchar(2000);
ds_obs_concat_w		varchar(2000);


BEGIN

ds_observacao_w := substr(' '||ds_observacao_p,1,2000);

select	substr(ds_observacao,1,2000)
into STRICT	ds_obs_concat_w
from	agenda_consulta
where	nr_sequencia = nr_seq_agenda_p;

update	agenda_consulta
set	ds_observacao 	= substr(ds_obs_concat_w || ds_observacao_w,1,2000),
	nm_usuario	= nm_usuario_p,
	dt_atualizacao	= clock_timestamp()
where	nr_sequencia	= nr_seq_agenda_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
