CREATE OR REPLACE PROCEDURE atualiza_ordem_compra_item (nr_ordem_compra_p bigint, cd_material_p bigint, ds_observacao_p text, nm_usuario_p text) AS $body$
BEGIN

	update	ordem_compra_item
	set	ds_observacao	= substr(ds_observacao || ds_observacao_p, 1, 255),
		dt_atualizacao	= clock_timestamp(),
		nm_usuario	= nm_usuario_p
