SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualiza_ordenacao_agenda ( cd_tipo_agenda_p bigint) AS $body$
DECLARE

					 
cd_agenda_w	bigint;
nr_ordenacao_w	bigint;
					
c01 CURSOR FOR 
	SELECT	a.cd_agenda 
	from	agenda a 
	where	a.ie_situacao = 'A' 
	and	a.cd_tipo_agenda = cd_tipo_agenda_p 
	order	by a.ds_agenda;
	

BEGIN 
 
nr_ordenacao_w := 1;
 
open	c01;
loop 
fetch	c01 into 
	cd_agenda_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin 
	 
	update 	agenda 
	set 	nr_seq_apresent = nr_ordenacao_w 
	where 	cd_agenda = cd_agenda_w;
	 
	nr_ordenacao_w := nr_ordenacao_w + 1;
	 
	end;
end loop;
close c01;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE atualiza_ordenacao_agenda ( cd_tipo_agenda_p bigint) FROM PUBLIC;

